import requests, json
from urllib import parse

class get_somethings():
    def __init__(self,tel='13999999999',pwd='21218cca77804d2ba1922c33e0151105',appcode='ebcapp'):
        self.tel = tel
        self.pwd = pwd
        self.appcode = appcode

    def get_jsessionid(self):
        url = "http://api.ebopay.cn/api_cloud/api/usersys/login"
        header = {
            "Content-Type" :"application/json"
        }
        data = {"mobile" :self.tel ,"password" :self.pwd ,"unionId" :"" ,"appCode" :self.appcode}
        response = requests.put(url ,data=json.dumps(data) ,headers=header)
        json_res = response.json()
        jsessionid = json_res['msg']
        return jsessionid


    def get_userinfo(self):
        url = "http://api.ebopay.cn/api_cloud/api/usersys/login"
        header = {
            "Content-Type" :"application/json"
        }
        data = {"mobile" :self.tel,"password" :self.pwd,"unionId" :"" ,"appCode" :self.appcode}
        response = requests.put(url ,data=json.dumps(data) ,headers=header)
        newresponse = response.status_code
        json_res = response.json()
        return newresponse,json_res



def getSMS_fromwebpage(num):
    url = "http://api.ebopay.cn:8001/message/list"

    header = {
        "Host":"api.ebopay.cn:8001",
        "Connection":"keep-alive",
        # "Content-Length":"28",
        #Upgrade-Insecure-Requests: 1
        "User-Agent":"Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/68.0.3440.106 Safari/537.36",
        "Accept":"text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,image/apng,*/*;q=0.8",
        "Referer":"http://api.ebopay.cn:8001/",
        "Accept-Language":"zh-CN,zh;q=0.9",
        "Cookie":"; shiroCookie=1dae0f6b-825a-43ad-9447-6919ab1558f2",
        #Accept-Encoding: gzip, deflate
    }

    formdata = {
        "order":"desc",
        "offset":"0",
        "limit":"9"
    }

    response = requests.post(url,data=formdata,headers=header)
    html1 = json.loads(response.text)
    verifycode = html1["rows"]
    verifycode.reverse()   #倒转列表
    # print(verifycode)
    #转换手机号码
    newsnum = ''
    conunt = 0
    for i in num:
        conunt += 1
        if conunt < 4:
            newsnum += i
        else:
            if conunt > 7:
                newsnum += i
            else:
                newsnum += "*"

    iwant_dict = None
    for i in verifycode:
        if i['mobile'] == newsnum:
            iwant_dict = i

    if iwant_dict == None:
        print('没找到')
    else:
        # print(iwant_dict['validateCode'])
        return iwant_dict['validateCode']